279 // Adaptive LIF Neuron #{neuron_id}
280 module adaptive_lif_neuron_{neuron_id} #(
281     parameter V_THRESH_BASE = {thresh_fp},
282     parameter V_RESET = {reset_fp},
283     parameter DECAY_FACTOR = {decay_fp},
284     parameter ADAPT_DECAY = {adapt_decay_fp},
285     parameter DELTA_THRESH = {delta_thresh_fp}
286 ) (
287     input wire clk,
288     input wire rst,
289     input wire [15:0] current_in,
290     input wire spike_in_valid,
291     output reg spike_out,
292     output reg [15:0] voltage_out,
293     output reg [15:0] threshold_out
294 );
295 
296     // Internal state
297     reg signed [23:0] membrane_voltage;
298     reg [23:0] adaptive_threshold;
299     reg [23:0] current_threshold;
300     
301     always @(posedge clk) begin
302         if (rst) begin
303             membrane_voltage <= 0;
304             adaptive_threshold <= 0;
305             current_threshold <= V_THRESH_BASE << 8;
306             spike_out <= 1'b0;
307         end else begin
308             spike_out <= 1'b0;
309             
310             // Update current threshold (base + adaptive component)
311             current_threshold <= (V_THRESH_BASE << 8) + adaptive_threshold;
312             
313             // Membrane voltage decay
314             membrane_voltage <= (membrane_voltage * DECAY_FACTOR) >> 8;
315             
316             // Adaptive threshold decay
317             adaptive_threshold <= (adaptive_threshold * ADAPT_DECAY) >> 8;
318             
319             // Add input current
320             if (spike_in_valid) begin
321                 membrane_voltage <= membrane_voltage + (current_in << 8);
322             end
323             
324             // Check for spike threshold
325             if (membrane_voltage >= current_threshold) begin
326                 spike_out <= 1'b1;
327                 membrane_voltage <= V_RESET << 8;
328                 // Increase adaptive threshold
329                 adaptive_threshold <= adaptive_threshold + (DELTA_THRESH << 8);
330             end
331         end
332     end
333     
334     assign voltage_out = membrane_voltage[23:8];
335     assign threshold_out = current_threshold[23:8];
336 
337 endmodule
338 '''
339         return hdl_code.strip()

